const morphOnHover = (character, isEntering) => {
  const duration = 0.3;
  const targetId = `${character}_puff`;

  if (isEntering) {
    gsap.to(`#${character}_reg`, {
      morphSVG: `#${targetId}`,
      duration,
      ease: "power3.inOut",
    });
  } else {
    gsap.to(`#${character}_reg`, {
      morphSVG: initialPathData.value[character],
      duration,
      ease: "power3.inOut",
    });
  }
};
